var context = require('./context');
var gl      = context.gl;

//▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄
function IndexBuffer(size) {
	this.uShortView   = new Uint16Array(size);
	this.binder       = gl.createBuffer();
	this.length       = 0;
	this.vertexIndice = 0;
}

module.exports = IndexBuffer;

//▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄
IndexBuffer.prototype.uploadData = function () {
	gl.bindBuffer(gl.ELEMENT_ARRAY_BUFFER, this.binder);
	gl.bufferData(gl.ELEMENT_ARRAY_BUFFER, this.uShortView, gl.STATIC_DRAW);
};

//▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄
IndexBuffer.prototype.bindBuffer = function () {
	gl.bindBuffer(gl.ELEMENT_ARRAY_BUFFER, this.binder);
};

//▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄▄
IndexBuffer.prototype.reset = function () {
	this.length       = 0;
	this.vertexIndice = 0;
};
